SPDataList.common:CreateProcedureSQL:
create procedure BBDW.[USR_USP_DATALIST_UNPAIDINSTALLMENTS] (@STARTDATE datetime = null, @ENDDATE datetime = null)
as set nocount on;
declare @DATERANGE int set @DATERANGE = datediff(d,@STARTDATE, @ENDDATE);
if @DATERANGE > 700 or @DATERANGE is null
--Do Year begin
select d.[CALENDARYEAR] as [DATEFIELD], sum(rgi.[RECURRINGGIFTINSTALLMENTAMOUNT]) as [AMOUNT] from BBDW.[FACT_RECURRINGGIFTINSTALLMENT] rgi inner join BBDW.[DIM_DATE] d on rgi.[RECURRINGGIFTINSTALLMENTDATEDIMID] = d.[DATEDIMID] where rgi.[ISMISSEDINSTALLMENT] = 1 and (@STARTDATE is null or rgi.[RECURRINGGIFTINSTALLMENTDATE] > @STARTDATE) and (@ENDDATE is null or rgi.[RECURRINGGIFTINSTALLMENTDATE] < @ENDDATE) group by d.[CALENDARYEAR] order by d.[CALENDARYEAR];
end
if @DATERANGE between 200 and 700
--Do Month begin
select d.[CALENDARMONTHYEARNAME] as [DATEFIELD], sum(rgi.[RECURRINGGIFTINSTALLMENTAMOUNT]) as [AMOUNT] from BBDW.[FACT_RECURRINGGIFTINSTALLMENT] rgi inner join BBDW.[DIM_DATE] d on rgi.[RECURRINGGIFTINSTALLMENTDATEDIMID] = d.[DATEDIMID] where rgi.[ISMISSEDINSTALLMENT] = 1 and (@STARTDATE is null or rgi.[RECURRINGGIFTINSTALLMENTDATE] > @STARTDATE) and (@ENDDATE is null or rgi.[RECURRINGGIFTINSTALLMENTDATE] < @ENDDATE) group by d.[CALENDARMONTHYEARNAME], d.[CALENDARYEARMONTH] order by d.[CALENDARYEARMONTH];
end
if @DATERANGE between 32 and 200
--Do Week begin
select d.[CALENDARWEEKYEARNAME] as [DATEFIELD], sum(rgi.[RECURRINGGIFTINSTALLMENTAMOUNT]) as [AMOUNT] from BBDW.[FACT_RECURRINGGIFTINSTALLMENT] rgi inner join BBDW.[DIM_DATE] d on rgi.[RECURRINGGIFTINSTALLMENTDATEDIMID] = d.[DATEDIMID] where rgi.[ISMISSEDINSTALLMENT] = 1 and (@STARTDATE is null or rgi.[RECURRINGGIFTINSTALLMENTDATE] > @STARTDATE) and (@ENDDATE is null or rgi.[RECURRINGGIFTINSTALLMENTDATE] < @ENDDATE) group by d.[CALENDARYEAR], d.[CALENDARWEEK], d.[CALENDARWEEKYEARNAME] order by d.[CALENDARYEAR], d.[CALENDARWEEK];
end
if @DATERANGE < 32
--Do Day begin
select d.[ACTUALDATESTRINGMDY] as [DATEFIELD], sum(rgi.[RECURRINGGIFTINSTALLMENTAMOUNT]) as [AMOUNT] from BBDW.[FACT_RECURRINGGIFTINSTALLMENT] rgi inner join BBDW.[DIM_DATE] d on rgi.[RECURRINGGIFTINSTALLMENTDATEDIMID] = d.[DATEDIMID] where rgi.[ISMISSEDINSTALLMENT] = 1 and (@STARTDATE is null or rgi.[RECURRINGGIFTINSTALLMENTDATE] > @STARTDATE) and (@ENDDATE is null or rgi.[RECURRINGGIFTINSTALLMENTDATE] < @ENDDATE) group by d.[ACTUALDATESTRINGMDY], d.[ACTUALDATE] order by d.[ACTUALDATE];
end
