SPDataList.c:CreateProcedureSQL:
create procedure BBDW.USR_USP_DATALIST_SUSTAINEDGIVINGUPGRADES ( @STARTDATE datetime = null, @ENDDATE datetime = null )
as set nocount on;
with [FIRSTGIFTS] as (select ft.[CONSTITUENTDIMID], ip.[RECURRINGGIFTINSTALLMENTPAYMENTAMOUNT] as [AMOUNT], ft.[FINANCIALTRANSACTIONDATE], row_number() over (partition by [CONSTITUENTDIMID] order by ft.[FINANCIALTRANSACTIONDATE] asc) as [ROW] from BBDW.[FACT_RECURRINGGIFTINSTALLMENTPAYMENT] ip inner join BBDW.[FACT_FINANCIALTRANSACTION] ft on ip.[FINANCIALTRANSACTIONFACTID] = ft.[FINANCIALTRANSACTIONFACTID] where (@STARTDATE is null or ft.[FINANCIALTRANSACTIONDATE] > @STARTDATE) and (@ENDDATE is null or ft.[FINANCIALTRANSACTIONDATE] < @ENDDATE)
),
[LASTGIFTS] as (select ft.[CONSTITUENTDIMID], ip.[RECURRINGGIFTINSTALLMENTPAYMENTAMOUNT] as [AMOUNT], ft.[FINANCIALTRANSACTIONDATE], row_number() over (partition by [CONSTITUENTDIMID] order by ft.[FINANCIALTRANSACTIONDATE] desc) as [ROW] from BBDW.[FACT_RECURRINGGIFTINSTALLMENTPAYMENT] ip inner join BBDW.[FACT_FINANCIALTRANSACTION] ft on ip.[FINANCIALTRANSACTIONFACTID] = ft.[FINANCIALTRANSACTIONFACTID] where (@STARTDATE is null or ft.[FINANCIALTRANSACTIONDATE] > @STARTDATE) and (@ENDDATE is null or ft.[FINANCIALTRANSACTIONDATE] < @ENDDATE) )
select c.[CONSTITUENTSYSTEMID] as [ID], c.[FULLNAME], fg.[AMOUNT] as [ORIGINALAMOUNT], lg.[AMOUNT] as [UPGRADEDAMOUNT] from [FIRSTGIFTS] fg inner join [LASTGIFTS] lg on fg.[CONSTITUENTDIMID] = lg.[CONSTITUENTDIMID] left join BBDW.[DIM_CONSTITUENT] c on c.[CONSTITUENTDIMID] = fg.[CONSTITUENTDIMID] where fg.[ROW] = 1 and lg.[ROW] = 1 and fg.[FINANCIALTRANSACTIONDATE] <> lg.[FINANCIALTRANSACTIONDATE] and lg.[AMOUNT] > fg.[AMOUNT]
